INSERT INTO `base_module_award` (`id`, `module_sub_id`, `type`, `order`, `open_time`, `info`, `stars`, `award_goods`) VALUES
(1, 55, 1, 1, '', '每日登陆游戏签到，签到的次数越多可领取更加丰厚的铜钱、元宝、声望和道具奖励。', '2#4,3#2,4#3,5#2', '2108,2115,2116,2117,3123,3124,3125'),
(2, 16, 1, 2, '', '等级达到15级开启，可消耗元宝获得大量的铜钱，等级越高获得的铜钱越多。', '2#4', ''),
(3, 32, 1, 3, '', '等级达到16级后开启，每天可领取一次俸禄奖励，官阶越高，可领取的铜钱奖励越多。', '2#3', ''),
(4, 54, 1, 4, '', '等级达到17级开启，每天可参拜关公一次，有几率获得高级装备护符与战魂卷轴。', '5#3', '1145,1146,1151,1152,1157,1158,1128,1131,1132'),
(5, 45, 1, 5, '', '等级达到20级开启，掠夺其他玩家可获得铜钱奖励。人在江湖飘，哪能不挨刀，你可要小心对方的反击。', '2#3', ''),
(6, 14, 1, 6, '', '等级达到21级且加入军团后开启，捐献可获得大量声望奖励。Vip达到{font~color=''#22ff00''}3{/font}级可开启高级捐献。', '3#3', ''),
(7, 11, 1, 7, '', '等级达到23级后开启，挑战其他玩家可获得声望奖励，每天可领取竞技场排名奖励。排名等级越高，奖励更加的丰厚。', '2#4,3#4,5#3', '3123,3124,3125,2108'),
(8, 31, 1, 8, '', '等级达到25级后开启，完成对应的悬赏任务可获得大量银币奖励。', '2#3', ''),
(9, 48, 1, 9, '', '等级达到29级开启，根据运送船只的等级获取奖励。可获得铜钱、声望、和洗练石奖励。', '2#4,3#4,5#2', '2114,2115,2116,2117'),
(10, 36, 1, 10, '', '等级达到33级开启，每半小时增加一次购买次数，可使用元宝/金币购买宝箱、钥匙、战魂碎片等稀有物品。。', '5#5', '3123,3126,2107,2114,2142,2143,2148'),
(11, 29, 1, 11, '', '等级达到34级开启，种植可获得铜钱、声望、元宝奖励，可别忘记收获。官阶达到指定级别，可开启高级农场！', '2#3,3#2,4#5', ''),
(12, 47, 1, 12, '', '等级达到38级且加入军团后开启，体现团队互帮互助的时刻到了，要获得大量声望奖励，请呼唤你的小伙伴一起参加吧。', '3#3', ''),
